-- Migration: Seed 200+ DIY repair guides for George AI
-- Date: 2026-02-18

-- Create the diy_repair_guides table
CREATE TABLE IF NOT EXISTS diy_repair_guides (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  category TEXT NOT NULL,
  title TEXT NOT NULL,
  difficulty INTEGER NOT NULL CHECK (difficulty BETWEEN 1 AND 5),
  time_estimate TEXT NOT NULL,
  tools_needed JSONB NOT NULL DEFAULT '[]',
  materials_needed JSONB NOT NULL DEFAULT '[]',
  steps JSONB NOT NULL DEFAULT '[]',
  safety_level TEXT NOT NULL CHECK (safety_level IN ('green', 'yellow', 'red')),
  when_to_call_pro TEXT,
  cost_estimate TEXT,
  search_keywords TEXT[],
  created_at TIMESTAMPTZ NOT NULL DEFAULT now()
);

CREATE INDEX IF NOT EXISTS idx_drg_category ON diy_repair_guides(category);
CREATE INDEX IF NOT EXISTS idx_drg_difficulty ON diy_repair_guides(difficulty);
CREATE INDEX IF NOT EXISTS idx_drg_safety ON diy_repair_guides(safety_level);
CREATE INDEX IF NOT EXISTS idx_drg_keywords ON diy_repair_guides USING GIN(search_keywords);

