INSERT INTO diy_repair_guides (category, title, difficulty, time_estimate, tools_needed, materials_needed, steps, safety_level, when_to_call_pro, cost_estimate, search_keywords) VALUES

('Plumbing', 'Fix a Running Toilet', 1, '15-30 minutes',
 '["adjustable wrench", "toilet flapper removal tool (optional)"]',
 '["replacement flapper ($5-8)", "toilet fill valve (if needed, $8-15)"]',
 '[{"instruction": "Turn off the water supply valve behind the toilet by turning clockwise.", "safety_tip": "Place towels around the base in case of minor spills."},
   {"instruction": "Flush the toilet to drain the tank.", "safety_tip": "The water in the tank is clean but cold."},
   {"instruction": "Remove the old flapper by unhooking it from the overflow tube.", "safety_tip": "Note the flapper size — bring the old one to the store for matching."},
   {"instruction": "Install the new flapper, hooking it onto the overflow tube ears.", "safety_tip": "Ensure the chain has about 1/2 inch of slack."},
   {"instruction": "Turn the water back on and test with a flush.", "safety_tip": "Check for leaks around the base after restoring water."}]',
 'green', 'If water is leaking from the base (may need wax ring), or if the fill valve is corroded and won''t shut off.', '$5-15',
 ARRAY['running toilet', 'toilet won''t stop running', 'toilet flapper', 'toilet keeps running', 'phantom flush']),

('Plumbing', 'Fix a Dripping Faucet', 2, '30-60 minutes',
 '["adjustable wrench", "Phillips screwdriver", "flat screwdriver", "Allen wrench set"]',
 '["replacement cartridge or O-rings ($5-15)", "plumber''s grease"]',
 '[{"instruction": "Turn off hot and cold water supply valves under the sink.", "safety_tip": "Open the faucet to release residual pressure."},
   {"instruction": "Remove the faucet handle by locating and removing the set screw (often under a decorative cap).", "safety_tip": "Place a rag in the drain to prevent losing small parts."},
   {"instruction": "Remove the cartridge or stem assembly.", "safety_tip": "Note the orientation for reassembly."},
   {"instruction": "Replace O-rings or the entire cartridge. Apply plumber''s grease to new O-rings.", "safety_tip": "Use manufacturer-specific replacement parts when possible."},
   {"instruction": "Reassemble in reverse order, turn on water, and test.", "safety_tip": "Tighten gradually — overtightening can crack fittings."}]',
 'green', 'If the faucet body is corroded, cracked, or if you can''t identify the cartridge type.', '$5-20',
 ARRAY['dripping faucet', 'leaky faucet', 'faucet drip', 'kitchen faucet leak', 'bathroom faucet drip']),

('Plumbing', 'Unclog a Drain', 1, '15-45 minutes',
 '["plunger", "drain snake or zip-it tool", "bucket"]',
 '["baking soda", "white vinegar", "boiling water"]',
 '[{"instruction": "Try boiling water first — pour slowly in 2-3 stages, waiting a few seconds between pours.", "safety_tip": "Use oven mitts when handling boiling water. Keep children away."},
   {"instruction": "If boiling water fails, pour 1/2 cup baking soda followed by 1/2 cup vinegar. Wait 15 minutes, then flush with hot water.", "safety_tip": "Do NOT use this method if you''ve already poured commercial drain cleaner — chemical reactions can occur."},
   {"instruction": "For stubborn clogs, use a plunger with enough water to cover the cup. Plunge vigorously 15-20 times.", "safety_tip": "Block the overflow hole with a wet rag for better suction."},
   {"instruction": "If plunging fails, insert a drain snake and rotate clockwise while pushing forward.", "safety_tip": "Wear rubber gloves — drain contents are unsanitary."}]',
 'green', 'If multiple drains are clogged simultaneously (main line issue) or if you smell sewage.', '$0-15',
 ARRAY['clogged drain', 'slow drain', 'blocked drain', 'drain clog', 'sink won''t drain']),

('Plumbing', 'Replace a Showerhead', 1, '10-15 minutes',
 '["adjustable wrench or pliers", "rag or cloth"]',
 '["new showerhead ($10-50)", "Teflon tape"]',
 '[{"instruction": "Wrap rag around the shower arm to protect the finish, then unscrew the old showerhead counterclockwise.", "safety_tip": "Hold the shower arm steady so you don''t break it inside the wall."},
   {"instruction": "Clean old Teflon tape and debris from the shower arm threads.", "safety_tip": "Inspect the arm for corrosion or damage."},
   {"instruction": "Wrap 3-5 turns of Teflon tape clockwise around the threads.", "safety_tip": "Wrap in the direction of threading to prevent unraveling."},
   {"instruction": "Hand-tighten the new showerhead, then snug with a wrench (1/4 turn past hand tight).", "safety_tip": "Don''t overtighten — you can crack the fitting."},
   {"instruction": "Turn on water and check for leaks at the connection.", "safety_tip": "If it leaks, remove and re-apply Teflon tape."}]',
 'green', 'If the shower arm is broken or corroded inside the wall.', '$10-50',
 ARRAY['showerhead replacement', 'change showerhead', 'new showerhead', 'shower head swap']),

('Plumbing', 'Replace a Toilet Flapper', 1, '10-15 minutes',
 '["none required"]',
 '["universal toilet flapper ($5-8)"]',
 '[{"instruction": "Turn off the water supply valve and flush to empty the tank.", "safety_tip": "Sponge out remaining water if needed."},
   {"instruction": "Unhook the old flapper from the overflow tube pegs and disconnect the chain from the flush lever.", "safety_tip": "Take the old flapper to the hardware store to match the size."},
   {"instruction": "Hook the new flapper onto the overflow tube pegs.", "safety_tip": "Ensure it seats evenly over the flush valve opening."},
   {"instruction": "Connect the chain to the flush lever with about 1/2 inch of slack.", "safety_tip": "Too much slack causes weak flushes; too little prevents proper sealing."},
   {"instruction": "Turn water back on and test flush.", "safety_tip": "Add a few drops of food coloring to the tank — if color appears in bowl without flushing, flapper isn''t sealing."}]',
 'green', 'If the flush valve seat is warped or pitted — the flapper can''t seal against a damaged seat.', '$5-8',
 ARRAY['toilet flapper', 'replace flapper', 'toilet won''t stop running', 'leaky toilet flapper']),

('Plumbing', 'Replace a Toilet Supply Line', 1, '15-20 minutes',
 '["adjustable wrench", "bucket", "towels"]',
 '["braided stainless steel supply line ($8-12)"]',
 '[{"instruction": "Turn off the water supply valve and flush the toilet to empty the tank.", "safety_tip": "Place a bucket and towels under the connection."},
   {"instruction": "Disconnect the old supply line from both the shutoff valve and the toilet tank.", "safety_tip": "Water will drip out — have the bucket ready."},
   {"instruction": "Connect the new braided supply line to the shutoff valve (hand-tighten, then 1/4 turn with wrench).", "safety_tip": "Don''t cross-thread — start by hand first."},
   {"instruction": "Connect the other end to the toilet fill valve. Hand-tighten plus 1/4 turn.", "safety_tip": "Braided stainless steel is much more reliable than plastic lines."},
   {"instruction": "Turn on water slowly and check both connections for leaks.", "safety_tip": "Check again after 10 minutes — slow leaks can take time to appear."}]',
 'green', 'If the shutoff valve itself is leaking or won''t turn.', '$8-12',
 ARRAY['toilet supply line', 'toilet water line', 'replace supply line', 'toilet hose replacement']),

('Plumbing', 'Reset a Garbage Disposal', 1, '5 minutes',
 '["flashlight"]',
 '["none"]',
 '[{"instruction": "Turn OFF the disposal switch and ensure it''s not running.", "safety_tip": "NEVER put your hand inside a garbage disposal, even when off."},
   {"instruction": "Look for the red reset button on the bottom of the disposal unit under the sink.", "safety_tip": "Use a flashlight to see clearly."},
   {"instruction": "Press the reset button firmly until it clicks and stays in.", "safety_tip": "If it pops back out immediately, wait 10 minutes for the motor to cool."},
   {"instruction": "Turn on cold water and flip the disposal switch.", "safety_tip": "If it hums but doesn''t spin, it''s jammed — not an electrical issue."}]',
 'green', 'If the disposal leaks from the bottom (seal failure) or makes grinding metal sounds.', '$0',
 ARRAY['garbage disposal reset', 'disposal not working', 'garbage disposal button', 'InSinkErator reset']),

('Plumbing', 'Unjam a Garbage Disposal', 1, '10-15 minutes',
 '["1/4-inch Allen wrench (hex key)", "flashlight", "tongs or pliers"]',
 '["none"]',
 '[{"instruction": "Turn OFF the disposal and unplug it (or turn off the breaker).", "safety_tip": "NEVER put fingers inside the disposal — use tongs to remove objects."},
   {"instruction": "Insert a 1/4-inch Allen wrench into the hex socket on the bottom center of the disposal.", "safety_tip": "Most disposals come with this wrench — check under the sink."},
   {"instruction": "Work the wrench back and forth to free the impellers.", "safety_tip": "You should feel it loosen and move freely."},
   {"instruction": "Use tongs or pliers to remove any debris from the top opening.", "safety_tip": "Common jams: bones, fruit pits, silverware, glass."},
   {"instruction": "Press the reset button, restore power, run cold water, and test.", "safety_tip": "Always run cold water when operating the disposal."}]',
 'green', 'If the disposal is leaking, cracked, or the motor is burned out (smells like burnt electrical).', '$0',
 ARRAY['jammed garbage disposal', 'disposal jammed', 'garbage disposal stuck', 'disposal humming']),

('Plumbing', 'Fix an Under-Sink Leak', 2, '20-45 minutes',
 '["adjustable wrench", "channel-lock pliers", "bucket", "flashlight"]',
 '["Teflon tape", "plumber''s putty", "replacement P-trap ($5-10, if needed)"]',
 '[{"instruction": "Place a bucket under the leak area and identify the source — run water and watch.", "safety_tip": "Dry everything first so you can see exactly where water appears."},
   {"instruction": "For leaks at slip-joint connections, try tightening the slip nuts by hand, then 1/4 turn with pliers.", "safety_tip": "Wrap pliers with a rag to avoid scratching chrome fittings."},
   {"instruction": "If tightening doesn''t work, disassemble the connection, clean threads, and apply Teflon tape or replace the washer.", "safety_tip": "Keep the bucket in place during disassembly."},
   {"instruction": "For leaks at the strainer/basket, remove and re-apply plumber''s putty.", "safety_tip": "Roll putty into a rope and press around the flange before reinserting."},
   {"instruction": "Reassemble, run water for 2 minutes, and check with a dry paper towel.", "safety_tip": "Check again the next day — some leaks are intermittent."}]',
 'green', 'If pipes are corroded, if the leak is inside the wall, or if it''s a supply line leak you can''t stop.', '$0-15',
 ARRAY['under sink leak', 'leaking under sink', 'P-trap leak', 'kitchen sink leak', 'drain pipe leak']),

('Plumbing', 'Winterize a Hose Bib', 1, '15-20 minutes',
 '["adjustable wrench (if removing hose)"]',
 '["insulated faucet cover ($3-5)", "towels"]',
