('Plumbing', 'Replace a Toilet Wax Ring', 3, '60-90 minutes',
 '["adjustable wrench", "putty knife", "level", "rubber gloves"]',
 '["wax ring ($5-8)", "toilet bolts ($3-5)", "caulk (optional)"]',
 '[{"instruction": "Turn off water, flush, sponge tank dry, disconnect supply line.", "safety_tip": "Stuff a rag in the drain opening to block sewer gases."},
   {"instruction": "Remove the bolt caps and unscrew the toilet mounting bolts.", "safety_tip": "If bolts are corroded, use a hacksaw to cut them."},
   {"instruction": "Lift the toilet straight up and set it on cardboard or a towel (it''s heavy — 50-80 lbs).", "safety_tip": "Lift with your legs, not your back. Get a helper if needed."},
   {"instruction": "Scrape off the old wax ring from the toilet base and the flange.", "safety_tip": "Wear rubber gloves — this is nasty work."},
   {"instruction": "Place the new wax ring on the flange (or on the toilet horn). Set the toilet straight down onto the bolts.", "safety_tip": "Press down firmly with a slight rocking motion. Don''t lift and reset — you''ll break the seal."},
   {"instruction": "Tighten bolts alternately, reconnect supply, and test with several flushes.", "safety_tip": "Don''t overtighten bolts — you''ll crack the porcelain."}]',
 'yellow', 'If the flange is broken, below floor level, or if the floor around the toilet is soft/rotted.', '$10-20',
 ARRAY['wax ring', 'toilet wax ring', 'toilet leaking base', 'toilet rocking', 'replace wax ring']),

('Plumbing', 'Fix a Slow Drain (Maintenance)', 1, '10-15 minutes',
 '["none"]',
 '["baking soda", "white vinegar", "boiling water"]',
 '[{"instruction": "Pour 1/2 cup baking soda directly into the drain.", "safety_tip": "Do not use with chemical drain cleaners — dangerous reaction."},
   {"instruction": "Follow with 1/2 cup white vinegar. Cover the drain and wait 15-30 minutes.", "safety_tip": "The fizzing action breaks down organic buildup."},
   {"instruction": "Flush with a full kettle of boiling water.", "safety_tip": "Pour slowly in PVC pipes — boiling water can soften PVC if poured too fast."},
   {"instruction": "Repeat monthly as maintenance.", "safety_tip": "This is prevention — for full clogs, you may need a snake."}]',
 'green', 'If the drain is completely stopped or if multiple drains are slow (main line issue).', '$0-3',
 ARRAY['slow drain', 'drain maintenance', 'slow sink drain', 'drain cleaning natural']),

('Plumbing', 'Replace a Bath Drain Stopper', 1, '15-20 minutes',
 '["pliers", "screwdriver", "adjustable wrench"]',
 '["replacement drain stopper ($8-15)"]',
 '[{"instruction": "Identify your stopper type: lift-and-turn, push-pull, pop-up, or toe-touch.", "safety_tip": "Take a photo and bring it to the store for matching."},
   {"instruction": "Remove the old stopper (lift-and-turn: turn counterclockwise; push-pull: pull up and unscrew; toe-touch: unscrew counterclockwise).", "safety_tip": "Some are held by a set screw on the side."},
   {"instruction": "Clean the drain opening and crossbar.", "safety_tip": "Remove hair and buildup while you have access."},
   {"instruction": "Install the new stopper per its type. Test that it holds water.", "safety_tip": "Fill the tub 2 inches to verify the seal."}]',
 'green', 'If the drain body itself is damaged or corroded.', '$8-15',
 ARRAY['bath drain stopper', 'tub stopper', 'bathtub drain', 'replace tub stopper']),

('Plumbing', 'Connect an Ice Maker Water Line', 2, '30-45 minutes',
 '["adjustable wrench", "drill with 1/4-inch bit (if no existing valve)", "tubing cutter"]',
 '["1/4-inch copper or braided supply line ($10-15)", "self-piercing saddle valve or add-a-tee valve ($8-15)"]',
 '[{"instruction": "Locate the nearest cold water supply line (usually under the kitchen sink or in the basement).", "safety_tip": "Turn off the water supply before tapping into any line."},
   {"instruction": "Install a saddle valve or (preferred) add-a-tee valve on the cold water line.", "safety_tip": "Add-a-tee valves are more reliable than self-piercing saddle valves."},
   {"instruction": "Run the tubing from the valve to the back of the refrigerator, leaving slack for pulling the fridge out.", "safety_tip": "Avoid kinking the tubing — use gentle curves."},
   {"instruction": "Connect to the fridge water inlet (usually a compression fitting on the back).", "safety_tip": "Hand-tighten compression fittings, then 1/4 turn with a wrench."},
   {"instruction": "Turn on water, check all connections for leaks, and wait 24 hours for ice.", "safety_tip": "Discard the first 2-3 batches of ice."}]',
 'yellow', 'If you need to run tubing through walls or if there''s no accessible cold water line nearby.', '$20-35',
 ARRAY['ice maker line', 'ice maker water line', 'fridge water line', 'connect ice maker']),

('Plumbing', 'Replace a Fridge Water Filter Line', 1, '10-15 minutes',
 '["none (most are tool-free)"]',
 '["replacement water filter ($15-40 — model specific)"]',
 '[{"instruction": "Locate the water filter (usually inside the fridge, in the base grille, or in the back).", "safety_tip": "Check your fridge manual or the filter itself for the part number."},
   {"instruction": "Turn the old filter counterclockwise (or push the release button) and pull out.", "safety_tip": "A small amount of water may drip — place a towel underneath."},
   {"instruction": "Remove the cap from the new filter (if applicable) and insert into the housing.", "safety_tip": "Make sure the O-ring is in place and lubricated."},
   {"instruction": "Turn clockwise or push until it clicks into place.", "safety_tip": "Run 2-3 gallons of water through the dispenser to flush the new filter."},
   {"instruction": "Reset the filter indicator light per your fridge''s instructions.", "safety_tip": "Replace every 6 months or per manufacturer recommendation."}]',
 'green', 'No need for a pro — very simple maintenance task.', '$15-40',
 ARRAY['fridge water filter', 'refrigerator filter', 'water filter replacement', 'fridge filter change']),

('Plumbing', 'Check an Expansion Tank', 2, '15-20 minutes',
 '["tire pressure gauge", "bicycle pump or air compressor"]',
 '["none (unless replacement needed, $30-50)"]',
 '[{"instruction": "Locate the expansion tank — a small tank connected to the cold water line near the water heater.", "safety_tip": "The tank should feel lighter on top (air) and heavier on bottom (water)."},
   {"instruction": "Tap the tank — the top should sound hollow, the bottom should sound full.", "safety_tip": "If the whole tank sounds full, the bladder has failed and needs replacement."},
   {"instruction": "Check the air pressure on the Schrader valve (top) with a tire gauge.", "safety_tip": "It should match your water pressure (typically 40-60 PSI)."},
   {"instruction": "Add air with a bike pump if pressure is low.", "safety_tip": "Turn off water supply before adding air for accurate reading."}]',
 'green', 'If the bladder has failed (tank completely waterlogged) or if you''re unsure of your water pressure.', '$0-50',
 ARRAY['expansion tank', 'water heater expansion tank', 'thermal expansion', 'pressure tank check']),

('Plumbing', 'Test a Sump Pump', 1, '10 minutes',
 '["bucket of water", "flashlight"]',
 '["none"]',
 '[{"instruction": "Locate your sump pump in the sump pit (usually in the basement).", "safety_tip": "Never reach into the pit while the pump is plugged in."},
   {"instruction": "Slowly pour a 5-gallon bucket of water into the pit.", "safety_tip": "Watch the float switch — the pump should activate automatically."},
   {"instruction": "Verify the pump turns on, removes the water, then shuts off.", "safety_tip": "If the pump runs continuously or doesn''t turn on, it needs service."},
   {"instruction": "Check the discharge pipe outside — water should be flowing away from the house.", "safety_tip": "Test quarterly and before heavy rain season."}]',
 'green', 'If the pump doesn''t activate, runs continuously, or makes unusual noises.', '$0',
 ARRAY['sump pump test', 'sump pump check', 'test sump pump', 'sump pump maintenance']),

