('Walls & Paint', 'Patch a Nail Hole', 1, '10-15 minutes',
 '["putty knife", "fine sandpaper (220 grit)"]',
 '["lightweight spackle ($5)", "touch-up paint"]',
 '[{"instruction": "Apply a small amount of spackle over the nail hole with a putty knife.", "safety_tip": "Less is more — overfilling means more sanding."},
   {"instruction": "Let dry completely (15-30 minutes for small holes).", "safety_tip": "Spackle shrinks slightly — you may need a second coat."},
   {"instruction": "Sand smooth with 220-grit sandpaper.", "safety_tip": "Sand gently — you don''t want to gouge the surrounding wall."},
   {"instruction": "Touch up with matching paint.", "safety_tip": "Feather the paint edges to blend with the surrounding wall."}]',
 'green', 'No need for a pro — one of the easiest DIY repairs.', '$5-10',
 ARRAY['nail hole', 'patch nail hole', 'wall hole repair', 'spackle nail hole']),

('Walls & Paint', 'Patch a Small Drywall Hole', 2, '30-60 minutes',
 '["putty knife", "drywall saw", "sandpaper (120 and 220 grit)", "paint roller or brush"]',
 '["drywall patch kit ($5-10) or mesh tape", "joint compound", "paint"]',
 '[{"instruction": "For holes up to 3 inches: apply a self-adhesive mesh patch over the hole.", "safety_tip": "Clean loose debris from the hole first."},
   {"instruction": "Apply joint compound over the patch with a wide putty knife, feathering the edges.", "safety_tip": "Apply thin coats — thick coats crack."},
   {"instruction": "Let dry completely (usually overnight). Apply a second thin coat and let dry.", "safety_tip": "Joint compound dust is messy — lay a drop cloth."},
   {"instruction": "Sand smooth starting with 120 grit, then 220 grit.", "safety_tip": "Wear a dust mask when sanding joint compound."},
   {"instruction": "Prime the patch and paint to match the wall.", "safety_tip": "Primer prevents the patch from showing through the paint."}]',
 'green', 'If the hole is larger than 6 inches, if there are water stains (possible leak), or if there''s mold.', '$5-15',
 ARRAY['drywall patch', 'wall hole repair', 'drywall repair', 'patch drywall', 'hole in wall']),

('Walls & Paint', 'Touch Up Paint', 1, '15-30 minutes',
 '["small brush or mini roller", "paint tray"]',
 '["matching paint", "painter''s tape (if needed)"]',
 '[{"instruction": "Clean the area to be touched up — remove dust, grease, or marks.", "safety_tip": "Ensure the surface is dry before painting."},
   {"instruction": "Stir paint thoroughly. If the paint is old, test on an inconspicuous area first.", "safety_tip": "Paint color can change over time — even the same can may not match a sun-faded wall."},
   {"instruction": "Apply paint with a small brush, feathering edges to blend.", "safety_tip": "For larger areas, use a mini roller for consistent texture."},
   {"instruction": "Apply a second coat if needed after the first dries.", "safety_tip": "If the touch-up is obvious, you may need to repaint the whole wall."}]',
 'green', 'No pro needed — basic maintenance.', '$0-15',
 ARRAY['touch up paint', 'paint touch up', 'wall touch up', 'paint repair']),

('Walls & Paint', 'Replace Caulk (Bathroom/Kitchen)', 2, '30-60 minutes',
 '["caulk gun", "utility knife", "caulk removal tool", "painter''s tape"]',
 '["silicone or latex caulk ($5-8)", "caulk remover (optional, $5)"]',
 '[{"instruction": "Remove old caulk with a utility knife and caulk removal tool.", "safety_tip": "Be careful not to scratch tile or tub surfaces."},
   {"instruction": "Clean the joint with rubbing alcohol and let dry completely.", "safety_tip": "Caulk won''t adhere to dirty or wet surfaces."},
   {"instruction": "Apply painter''s tape on both sides of the joint for clean lines.", "safety_tip": "Leave about 1/4 inch gap between the tape lines."},
   {"instruction": "Apply caulk in a steady bead. Smooth with a wet finger or caulk tool.", "safety_tip": "Use silicone caulk for wet areas — latex caulk for dry areas."},
   {"instruction": "Remove tape immediately before caulk skins over. Let cure 24 hours before water exposure.", "safety_tip": "Ensure good ventilation when using silicone caulk."}]',
 'green', 'If you see mold behind the old caulk (could indicate a leak) or if tile is loose.', '$5-15',
 ARRAY['replace caulk', 'caulk bathroom', 'caulk tub', 'recaulk shower', 'kitchen caulk']),

('Walls & Paint', 'Install Weather Stripping', 1, '20-30 minutes',
 '["scissors or utility knife", "tape measure", "clean rag"]',
 '["adhesive-backed weather stripping ($5-10)", "rubbing alcohol"]',
 '[{"instruction": "Clean the door or window frame where stripping will go with rubbing alcohol.", "safety_tip": "Surface must be clean and dry for adhesive to stick."},
   {"instruction": "Measure and cut weather stripping to length.", "safety_tip": "Cut slightly long and trim to fit — you can''t add length back."},
   {"instruction": "Peel the backing and press firmly into place along the frame.", "safety_tip": "Start at one end and work your way along — don''t stretch the material."},
   {"instruction": "Close the door/window to test the seal. Adjust if too tight or loose.", "safety_tip": "It should compress slightly when closed — not prevent closing."}]',
 'green', 'No pro needed — great energy savings project.', '$5-10',
 ARRAY['weather stripping', 'weatherstrip', 'door seal', 'window seal', 'draft stopper']),

('Walls & Paint', 'Install a Door Sweep', 1, '15-20 minutes',
