-- Pro Knowledge Base Seed Data (50 entries)
-- Categories: plumbing, electrical, hvac, appliances, general

INSERT INTO pro_knowledge_base (category, subcategory, title, content, specs, common_issues, tools_needed, tips, verified) VALUES
('plumbing', 'pipe_sizes', 'Residential Copper Pipe Sizing', 'Standard residential copper pipe comes in Type M (thin wall), Type L (medium), and Type K (thick wall). Most residential supply lines use 1/2" or 3/4" Type L.', '{"nominal_sizes": ["1/2 inch", "3/4 inch", "1 inch"], "types": ["M", "L", "K"], "pressure_rating_psi": 150}', '["Pinhole leaks in Type M from acidic water", "Green patina corrosion at joints", "Galvanic corrosion when connected to galvanized"]', '["Pipe cutter", "Propane torch", "Flux and solder", "Emery cloth", "Deburring tool"]', 'Always use lead-free solder for potable water. Heat the fitting, not the pipe. Let solder flow into joint by capillary action.', true),
('plumbing', 'pipe_sizes', 'PEX Tubing Guide', 'PEX (cross-linked polyethylene) is flexible plastic tubing for water supply. Available in PEX-A (most flexible), PEX-B (most common), and PEX-C.', '{"sizes": ["3/8 inch", "1/2 inch", "3/4 inch", "1 inch"], "max_temp_f": 200, "max_psi": 80, "colors": {"red": "hot", "blue": "cold", "white": "either"}}', '["Rodent damage", "UV degradation if exposed to sunlight", "Fitting restrictions vary by code"]', '["PEX crimping tool", "PEX cutter", "Crimp rings", "Go/no-go gauge"]', 'PEX-A with expansion fittings has the best flow rate. Never use PEX outdoors or where exposed to UV. Check local code for approved fitting types.', true),
('plumbing', 'cartridges', 'Moen 1222 Cartridge Replacement', 'The Moen 1222 is used in Moen Posi-Temp single-handle shower valves. Most common shower cartridge replacement in residential work.', '{"model": "1222", "brand": "Moen", "type": "Posi-Temp", "fits": "Single handle shower/tub valves post-2000"}', '["Cartridge stuck due to mineral buildup", "Leaking behind wall", "Temperature fluctuation", "Handle hard to turn"]', '["Moen cartridge puller (104421)", "Adjustable pliers", "Silicone grease", "Needle-nose pliers"]', 'Shut off water supply first. Use the Moen-specific puller — generic ones strip the cartridge. Apply silicone grease to new cartridge O-rings before inserting.', true),
('plumbing', 'cartridges', 'Delta RP19804 Cartridge', 'Delta Monitor 1300/1400 series shower valve cartridge. Extremely common in homes built 1980s-2010s.', '{"model": "RP19804", "brand": "Delta", "type": "Monitor 1300/1400", "material": "brass and plastic"}', '["Dripping when off", "Scald protection failure", "Worn seats and springs (RP4993)"]', '["Allen wrench set", "Adjustable pliers", "Seat removal tool", "Silicone grease"]', 'Always replace the seats and springs (RP4993) at the same time as the cartridge. They are the usual cause of drips, not the cartridge itself.', true),
('plumbing', 'toilet_parts', 'Fluidmaster 400A Fill Valve', 'Universal toilet fill valve. Fits most 2-piece toilets. Adjustable height 9"-14". Anti-siphon design meets code requirements.', '{"model": "400A", "brand": "Fluidmaster", "type": "Fill valve", "inlet": "7/8 inch ballcock", "height_range": "9 to 14 inches"}', '["Whistling noise from debris in valve", "Ghost flushing from worn seal", "Water level not adjusting properly"]', '["Adjustable wrench", "Channel locks", "Bucket", "Towels"]', 'Set water level to 1 inch below overflow tube. Replace supply line if corroded. The 400A is quieter than the 400H but the 400H has a faster fill rate for commercial use.', true),
('plumbing', 'toilet_parts', 'Korky 528 Flapper (2-inch)', 'Universal 2-inch toilet flapper. Fits most standard toilets with 2-inch flush valves. Adjustable for water-saving flush.', '{"model": "528", "brand": "Korky", "size": "2 inch", "material": "chlorine-resistant rubber"}', '["Running toilet from warped flapper", "Chain too tight or too loose", "Hard water mineral buildup on flapper seat"]', '["None required — hand install", "Clean cloth for wiping flush valve seat"]', 'Clean the flush valve seat before installing new flapper. Leave 1/2 inch of slack in the chain. If toilet still runs, the flush valve seat may need replacing.', true),
('plumbing', 'toilet_parts', 'Wax Ring vs Wax-Free Seal', 'Wax rings (Hercules Johni-Ring) are the traditional toilet seal. Wax-free options (Fluidmaster Better Than Wax) allow repositioning and work on uneven floors.', '{"wax_ring": {"brand": "Hercules", "model": "90210"}, "wax_free": {"brand": "Fluidmaster", "model": "7530"}, "flange_height": "1/4 inch above finished floor ideal"}', '["Wax ring fails if toilet rocks", "Double-stacking wax rings on high flanges", "Wax-free may not seal on very uneven surfaces"]', '["Putty knife", "Adjustable wrench", "Level", "Toilet shims"]', 'If the flange is more than 1/4 inch below floor, use an extra-thick wax ring or flange extender. Never double-stack standard wax rings. Wax-free is ideal for second-floor bathrooms where you want to verify the seal.', true),
('plumbing', 'pipe_sizes', 'PVC DWV Pipe Sizes', 'PVC Schedule 40 drain/waste/vent pipe. Standard sizes: 1-1/2" (lav drain), 2" (shower/tub), 3" (toilet), 4" (building drain/sewer).', '{"material": "PVC Schedule 40", "sizes": {"lavatory": "1.5 inch", "shower": "2 inch", "toilet": "3 inch", "main_drain": "4 inch"}, "slope": "1/4 inch per foot"}', '["Improper slope causing clogs", "Missing cleanouts", "Wrong primer/cement for PVC vs ABS"]', '["PVC cutter or hacksaw", "PVC primer and cement", "Level", "Measuring tape"]', 'Always prime before cementing PVC. Hold joints for 30 seconds after cementing. Slope drain lines 1/4 inch per foot minimum. Use long-sweep 90s instead of sharp 90s for drain lines.', true),
('plumbing', 'cartridges', 'Price Pfister 974-042 Cartridge', 'Ceramic disc cartridge for Price Pfister (now Pfister) single-handle kitchen and bath faucets. Avante and other series.', '{"model": "974-042", "brand": "Pfister", "type": "Ceramic disc", "hot_side": "left"}', '["Dripping from spout", "Handle loose or wobbly", "Difficulty turning handle"]', '["Allen wrench (3/32)", "Adjustable pliers", "Silicone grease"]', 'Pfister offers lifetime warranty — call 1-800-PFAUCET for free replacement cartridge before buying. Remove handle set screw from underneath the lever.', true),
('plumbing', 'pipe_sizes', 'SharkBite Push-Fit Fittings', 'Push-to-connect fittings for copper, PEX, and CPVC. No soldering or crimping required. Approved for behind-wall use in most jurisdictions.', '{"brand": "SharkBite", "sizes": ["1/2 inch", "3/4 inch", "1 inch"], "compatible": ["Copper", "PEX", "CPVC"], "max_psi": 200, "max_temp_f": 200}', '["Pipe not fully inserted causing leaks", "Pipe not deburred causing O-ring damage", "Not approved in all jurisdictions"]', '["SharkBite deburr/depth tool", "Pipe cutter", "SharkBite disconnect tool"]', 'Mark insertion depth on pipe before pushing in. Always deburr cut pipe ends. Keep disconnect tools accessible for future service. Check local code — some areas require soldered joints behind walls.', true),

('electrical', 'wire_gauge', 'Residential Wire Gauge Guide', 'Standard residential wiring: 14 AWG for 15A circuits, 12 AWG for 20A circuits, 10 AWG for 30A (dryer/AC), 8 AWG for 40A (range), 6 AWG for 50A.', '{"14_awg": "15A max", "12_awg": "20A max", "10_awg": "30A max", "8_awg": "40A max", "6_awg": "50A max", "material": "copper"}', '["Undersized wire for circuit amperage", "Aluminum wiring in older homes (fire risk)", "Backstab connections loosening over time"]', '["Wire strippers", "Voltage tester", "Multimeter", "Linesman pliers", "Screwdrivers"]', 'Never put 14 AWG on a 20A breaker. Use pigtails instead of backstab connections. When working on aluminum wiring, use CO/ALR rated devices and apply Noalox anti-oxidant compound.', true),
('electrical', 'wire_gauge', 'THHN vs Romex (NM-B) Wire', 'THHN is individual conductors pulled through conduit. Romex (NM-B) is sheathed cable for residential in-wall use. Romex cannot be used in conduit or exposed locations.', '{"romex": {"type": "NM-B", "use": "In-wall residential", "jacket": "PVC"}, "thhn": {"type": "Individual conductor", "use": "In conduit", "insulation": "PVC/Nylon"}}', '["Using Romex in exposed locations (code violation)", "Romex in conduit causing overheating", "Wrong wire type for wet locations"]', '["Fish tape", "Wire pulling lubricant", "Conduit bender", "Cable staples"]', 'Use UF-B (underground feeder) for direct burial. Use THWN-2 for wet locations. Romex must be stapled within 12 inches of each box and every 4.5 feet.', true),
('electrical', 'outlets', 'GFCI Outlet Installation', 'Ground Fault Circuit Interrupter outlets required in kitchens, bathrooms, garages, outdoors, and within 6 feet of water sources. Trips at 4-6mA ground fault.', '{"type": "GFCI", "trip_current_ma": 5, "required_locations": ["bathroom", "kitchen", "garage", "outdoor", "basement", "laundry"], "voltage": 120}', '["Tripping frequently from moisture", "Wired incorrectly (line vs load)", "GFCI not protecting downstream outlets", "End of life — wont reset"]', '["Voltage tester", "Screwdriver set", "Wire nuts", "GFCI outlet tester"]', 'LINE terminals connect to power source. LOAD terminals protect downstream outlets. Test monthly. Replace every 10 years. A single GFCI can protect multiple downstream outlets if wired correctly on LOAD side.', true),
('electrical', 'outlets', 'AFCI Breaker Requirements', 'Arc Fault Circuit Interrupters detect dangerous arcing. Required by NEC 2017+ in virtually all living spaces: bedrooms, living rooms, hallways, closets, kitchens.', '{"type": "AFCI", "detection": "Series and parallel arc faults", "required": "Most habitable rooms per NEC 2017+", "brands": ["Square D", "Eaton", "Siemens"]}', '["Nuisance tripping from motors or LED dimmers", "Incompatibility with some devices", "Cost concerns on older panel upgrades"]', '["Screwdriver", "Voltage tester", "Flashlight", "Panel directory"]', 'Dual-function AFCI/GFCI breakers are available and satisfy both requirements. If nuisance tripping, check for damaged wire insulation or loose connections before replacing breaker.', true),
('electrical', 'breakers', 'Panel Breaker Identification', 'Common residential panels: Square D QO and Homeline, Eaton BR and CH, Siemens/ITE, GE. Breakers are NOT interchangeable between brands.', '{"square_d_qo": "QO series (premium)", "square_d_hom": "Homeline (builder grade)", "eaton_br": "BR series (Type BR)", "eaton_ch": "CH series (bolt-on)", "siemens": "QP series", "ge": "THQL series"}', '["Wrong breaker brand installed (fire hazard)", "Double-tapped breakers", "Federal Pacific or Zinsco panels (known hazards)"]', '["Flashlight", "Screwdriver", "Voltage tester", "Breaker identification chart"]', 'NEVER mix breaker brands unless listed as classified (e.g., Eaton CL breakers fit multiple panels). Recommend full panel replacement for Federal Pacific (FPE) Stab-Lok and Zinsco panels.', true),
('electrical', 'breakers', 'Tandem/Slim Breakers', 'Tandem (slim/twin) breakers fit two circuits in one breaker slot. Not all panel slots accept tandems — check panel labeling.', '{"type": "Tandem/Twin", "width": "1 inch (same as single)", "circuits": 2, "common_models": ["Square D HOMT", "Eaton BRD", "Siemens Q2020"]}', '["Installed in non-tandem-rated slots", "Exceeding panel circuit count", "Overloading panel bus"]', '["Flashlight", "Screwdriver", "Panel schedule"]', 'Check the panel door diagram showing which slots accept tandems. Never exceed the panel maximum circuit count. Tandems cannot be AFCI or GFCI — use full-size breakers for those.', true),
('electrical', 'outlets', '240V Outlet Types', 'Common 240V receptacles: NEMA 6-20 (20A, no neutral), NEMA 10-30 (old dryer, no ground), NEMA 14-30 (modern dryer), NEMA 14-50 (range/EV charger).', '{"6_20": {"amps": 20, "wires": 3, "use": "Window AC, power tools"}, "14_30": {"amps": 30, "wires": 4, "use": "Dryer"}, "14_50": {"amps": 50, "wires": 4, "use": "Range, EV charger"}}', '["Old 3-prong dryer outlets (NEMA 10-30) lack ground", "Undersized wire for amperage", "Incorrect breaker size"]', '["Voltage tester", "Wire strippers", "Torque screwdriver", "Appropriate gauge wire"]', 'Modern code requires 4-wire (with ground) for 240V appliances. When updating a 3-prong dryer to 4-prong, you need a new cable — do not bootleg the ground to neutral.', true),
('electrical', 'wire_gauge', 'Low Voltage Wiring (Thermostat/Doorbell)', 'Thermostat wire: 18/5 or 18/8 for heat pumps. Doorbell: 18/2 with 16V transformer. Cat5e/Cat6 for network. All are Class 2 low voltage.', '{"thermostat": {"gauge": "18 AWG", "conductors": "5 or 8", "voltage": "24V AC"}, "doorbell": {"gauge": "18 AWG", "conductors": 2, "transformer": "16V"}}', '["Not enough conductors for heat pump systems", "Damaged wire from staples", "Transformer failed on doorbell"]', '["Wire strippers", "Low voltage tester", "Drill with bell hanger bit", "Fish tape"]', 'Always pull 18/8 for thermostats even if current system only needs 5 wires — future-proofs for heat pump or smart thermostat upgrades. Label wires at both ends.', true),
('electrical', 'breakers', 'Whole House Surge Protection', 'Type 2 SPD (Surge Protective Device) installed at main panel. Protects all circuits from external surges. Required by NEC 2020 for new construction.', '{"type": "Type 2 SPD", "protection_kA": "50-80", "brands": ["Eaton CHSPT2ULTRA", "Square D HEPD80", "Siemens FS140"], "installation": "Main panel"}', '["Not installed on dedicated 2-pole breaker", "Exceeded surge capacity (needs replacement)", "LED indicator not checked"]', '["Screwdriver", "Wire strippers", "2-pole breaker (per SPD specs)"]', 'Install as close to main breaker as possible with shortest wire leads. Keep leads under 6 inches for best protection. Check LED indicator annually — replace if no light.', true),

('hvac', 'filters', 'Air Filter MERV Ratings', 'MERV (Minimum Efficiency Reporting Value) rates filter effectiveness. MERV 1-4 minimal, 8 good residential, 11 better, 13 hospital-grade. Higher MERV = more airflow restriction.', '{"merv_8": "Dust, pollen, mold spores", "merv_11": "Pet dander, smog, fine dust", "merv_13": "Bacteria, smoke, virus carriers", "standard_sizes": ["16x20x1", "16x25x1", "20x20x1", "20x25x1"]}', '["Filter too restrictive for system (frozen coil)", "Wrong size filter allowing bypass", "Filter installed backwards"]', '["Tape measure", "Flashlight"]', 'Arrow on filter points toward airflow direction (toward blower). Most residential systems max out at MERV 11 without modification. Change every 1-3 months. 4-inch filters last 6-12 months.', true),
('hvac', 'filters', 'Common Filter Sizes and Locations', 'Filters located at return air grille or at air handler. Measure existing filter — nominal size is rounded up from actual size. 16x25x1 is most common residential size.', '{"most_common": ["16x25x1", "20x25x1", "16x20x1", "20x20x1", "14x25x1"], "depth_options": ["1 inch", "2 inch", "4 inch", "5 inch"]}', '["Customer buying wrong size", "No filter rack installed", "Multiple return vents with different sizes"]', '["Tape measure", "Flashlight", "Screwdriver for grille removal"]', 'Always measure the existing filter. Nominal sizes are 1/2 inch larger than actual. If customer wants better filtration, suggest upgrading to 4-inch media filter with filter rack modification.', true),
('hvac', 'refrigerants', 'R-410A Refrigerant Basics', 'R-410A (Puron) is the standard residential AC refrigerant since 2010. Operates at higher pressures than R-22. Systems are factory-charged and should not need refrigerant unless there is a leak.', '{"type": "R-410A", "high_side_psi": "350-400", "low_side_psi": "110-130", "required_cert": "EPA Section 608", "oil": "POE (Polyolester)"}', '["Overcharging causes high head pressure", "Undercharge from slow leak", "Mixing with R-22 (never!)", "Moisture contamination"]', '["Manifold gauges (rated for 410A)", "Refrigerant scale", "Leak detector", "Vacuum pump", "Micron gauge"]', 'R-410A gauges have different fittings than R-22 (5/16 vs 1/4). Always weigh refrigerant in — do not charge by pressure alone. System must be evacuated to 500 microns before charging.', true),
('hvac', 'refrigerants', 'R-22 Phase-Out Info', 'R-22 (Freon) was banned from new production/import Jan 1, 2020. Only reclaimed R-22 is available. Very expensive ($50-150/lb). Consider system replacement for major leaks.', '{"type": "R-22", "status": "Phase-out complete", "replacement": "R-407C (drop-in) or new R-410A system", "cost_per_lb": "$50-150"}', '["Homeowner sticker shock on R-22 pricing", "Drop-in refrigerants reducing efficiency", "Oil compatibility issues with retrofits"]', '["R-22 manifold gauges", "Refrigerant recovery machine", "Scale"]', 'If R-22 system has major leak, recommend full system replacement to R-410A. Small top-offs may be economical short-term. Never vent refrigerant — EPA fines up to $44,539 per day.', true),
('hvac', 'wiring', 'Thermostat Wire Colors', 'Standard thermostat wire colors: R (red)=24V power, W (white)=heat, Y (yellow)=cooling, G (green)=fan, C (blue)=common, O/B (orange)=heat pump reversing valve.', '{"R": "24V power (transform hot)", "W": "Heat call", "Y": "Cool call", "G": "Fan", "C": "Common (24V return)", "O": "Heat pump reversing (cool)", "B": "Heat pump reversing (heat)", "W2": "Stage 2 heat", "Y2": "Stage 2 cool"}', '["Missing C wire for smart thermostats", "Wires not matching standard colors", "Blown 3A fuse on control board"]', '["Screwdriver", "Voltage tester", "Phone camera (photo existing wiring)", "Wire labels"]', 'ALWAYS photograph existing thermostat wiring before disconnecting. If no C wire, use an add-a-wire kit (Venstar ACC0410) rather than stealing a wire. Check for 24V at R terminal to verify transformer.', true),
('hvac', 'wiring', 'Capacitor Testing and Replacement', 'Run capacitors keep compressor and fan motors running. Dual capacitors have 3 terminals: C (common), HERM (compressor), FAN. Rated in microfarads (µF).', '{"common_sizes": {"compressor": "35-60 µF", "fan": "5-10 µF", "dual": "45/5 or 35/5 µF"}, "voltage_rating": "370V or 440V", "tolerance": "+/- 6%"}', '["Bulging or leaking capacitor", "AC hums but wont start", "Fan spins slowly", "Capacitor more than 6% out of spec"]', '["Multimeter with capacitance mode", "Insulated screwdriver (to discharge)", "11/32 or 1/4 nut driver"]', 'ALWAYS discharge capacitor before handling — can hold lethal charge. Replace with same µF rating. Can use higher voltage rating (440V instead of 370V) but never lower. Dual capacitors must match both values.', true),
('hvac', 'filters', 'UV Light Air Purification', 'UV-C germicidal lights installed in ductwork or near evaporator coil kill mold, bacteria, and viruses. Bulbs need replacement every 12-24 months.', '{"type": "UV-C germicidal", "wavelength_nm": 254, "brands": ["RGF REME HALO", "Fresh-Aire UV", "Honeywell"], "bulb_life": "12-24 months"}', '["Bulb degraded but still lit (reduced effectiveness)", "Improper placement", "Not sized for ductwork"]', '["Screwdriver", "Safety glasses (never look at UV light)", "Drill"]', 'UV light should shine on evaporator coil to prevent mold growth. REME HALO is an active purifier that treats the whole home. Replace bulbs annually even if they still illuminate — UV output degrades.', true),
('hvac', 'wiring', 'Mini Split Installation Basics', 'Ductless mini splits: indoor wall unit + outdoor condenser connected by lineset (1/4" liquid, 3/8"-1/2" suction), communication wire, and condensate drain.', '{"lineset_liquid": "1/4 inch", "lineset_suction": "3/8 or 1/2 inch", "electrical": "Dedicated circuit", "refrigerant": "R-410A pre-charged", "brands": ["Mitsubishi", "Fujitsu", "Daikin", "LG"]}', '["Condensate drain clogged or improperly sloped", "Lineset kinks reducing efficiency", "Indoor unit not level"]', '["Flaring tool", "Torque wrenches (specific to line sizes)", "Vacuum pump", "Micron gauge", "Level", "Hole saw"]', 'Flare connections must be torqued to spec — most mini split leaks are from improper flaring. Run condensate drain with proper slope. Mitsubishi and Fujitsu are considered the most reliable. Always vacuum to 500 microns.', true),
('hvac', 'refrigerants', 'R-454B (Next Gen Refrigerant)', 'R-454B will replace R-410A starting 2025 due to lower GWP. Mildly flammable (A2L classification). New equipment required — not compatible with R-410A systems.', '{"type": "R-454B", "gwp": 466, "r410a_gwp": 2088, "classification": "A2L (mildly flammable)", "status": "Phasing in 2025+"}', '["Technician training needed for A2L handling", "New recovery equipment required", "Not a drop-in for R-410A"]', '["A2L-rated recovery machine", "Leak detector rated for A2L", "Refrigerant scale"]', 'Start training on A2L refrigerant handling now. R-410A systems will be serviced for decades but new installs will transition. Equipment rooms may need ventilation modifications for A2L.', true),

('appliance', 'error_codes', 'Samsung Washer Error Codes', 'Common Samsung washer errors: UE/UB=unbalanced load, SE/5E=drain issue, HE/4E=water supply, LE/IE=water level sensor, DC=door lock.', '{"UE": "Unbalanced load", "5E": "Drain pump blocked or hose kinked", "4E": "Water supply issue", "IE": "Water level sensor fault", "DC": "Door lock failure", "brand": "Samsung"}', '["5E code from clogged debris filter (check front bottom panel)", "UE from shipping bolts not removed", "4E from kinked inlet hoses"]', '["Torx screwdriver set", "Multimeter", "Towels", "Shallow pan"]', 'Samsung debris filter is behind the small door at bottom-front of washer. Clean it monthly. The 5E drain error is the #1 Samsung service call and usually just needs filter cleaning.', true),
('appliance', 'error_codes', 'LG Washer Error Codes', 'Common LG washer errors: OE=drain issue, IE=inlet water, UE=unbalanced, LE=motor locked, PE=water level sensor, CL=child lock active (not an error).', '{"OE": "Drain issue", "IE": "Inlet water problem", "UE": "Unbalanced load", "LE": "Motor locked/overloaded", "PE": "Pressure sensor", "CL": "Child lock (hold Temp + Options 3 sec)", "brand": "LG"}', '["OE from kinked drain hose", "CL mistaken for error (just child lock)", "LE from overloaded drum"]', '["Multimeter", "Towels", "Pliers", "Torx set"]', 'LG Smart ThinQ app can run remote diagnostics via phone speaker held to washer. Child Lock (CL) is the #1 unnecessary service call — hold specific button combo 3 seconds to clear.', true),
('appliance', 'error_codes', 'GE Dishwasher Error Codes', 'Common GE/GE Profile dishwasher codes: flashing lights pattern = specific error. Blinking Start/Clean: door not latched. Blinking Normal/Heavy: thermistor fault.', '{"blinking_start": "Door latch issue", "blinking_clean": "Water temperature fault", "flashing_all": "Control board reset needed", "brand": "GE"}', '["Door latch switch worn", "Spray arms clogged with debris", "Not draining (check garbage disposal knockout plug)"]', '["Torx T20 screwdriver", "Multimeter", "Toothpick for spray arm holes"]', 'New dishwasher connected to disposal: REMOVE the disposal knockout plug or dishwasher wont drain. This is the #1 new install callback. Reset GE dishwasher by turning off breaker for 1 minute.', true),
('appliance', 'parts', 'Refrigerator Compressor Start Relay', 'The start relay (and overload) clicks the compressor on. A failed relay causes clicking sounds then silence. Common on single-speed compressors.', '{"location": "Back of fridge, attached to compressor", "symptoms": "Clicking then shutting off", "common_part": "3-in-1 hard start kit", "brands": ["Whirlpool", "GE", "LG", "Samsung"]}', '["Relay burnt (rattle when shaken)", "Overload tripping from dirty condenser coils", "Compressor itself failed (rare)"]', '["1/4 inch nut driver", "Multimeter", "3-in-1 start kit (Supco RCO410)"]', 'Shake the old relay — if it rattles, its bad. A Supco RCO410 3-in-1 kit is a universal fix. ALWAYS clean condenser coils when replacing relay — dirty coils caused the overload that killed it.', true),
('appliance', 'parts', 'Dryer Thermal Fuse', 'The thermal fuse is a one-time safety device that blows when dryer overheats. Dryer runs but no heat. Located on blower housing or exhaust duct inside dryer.', '{"symptom": "Dryer runs but no heat", "cause": "Restricted airflow/clogged vent", "common_parts": {"whirlpool": "WP3392519", "samsung": "DC47-00016A"}, "resistance": "0 ohms (good) or OL (blown)"}', '["Blown fuse from clogged vent (fix root cause!)", "Fuse blows again shortly after replacement", "Vent run too long"]', '["Multimeter", "Nut driver set", "Vent brush kit", "Screwdriver"]', 'ALWAYS clean the dryer vent when replacing a thermal fuse — the clogged vent caused it to blow. If you just replace the fuse, it will blow again. Max vent run is 25 feet minus 5 feet per elbow.', true),
('appliance', 'error_codes', 'Whirlpool/Maytag Washer F-Codes', 'Whirlpool/Maytag front-load F-codes: F5E2=door lock, F8E1=water supply, F9E1=drain, F0E2=overloaded, F7E1=motor fault.', '{"F5E2": "Door lock failure", "F8E1": "No water detected", "F9E1": "Long drain time", "F0E2": "Oversuds or overloaded", "F7E1": "Motor speed sensing fault", "brand": "Whirlpool/Maytag"}', '["F9E1 from clogged drain pump filter", "F8E1 from turned-off water valves", "F0E2 from too much HE detergent"]', '["Torx T20", "Multimeter", "Towels", "Shallow pan"]', 'Whirlpool front-loaders have a drain pump filter behind the bottom-front panel. This is the first thing to check for F9E1 drain errors. Use only HE detergent — 1 tablespoon is usually enough.', true),
('appliance', 'parts', 'Garbage Disposal Jam Reset', 'When disposal hums but doesnt spin: its jammed. Use 1/4" hex wrench in bottom center hole to manually rotate. Red reset button on bottom resets overload.', '{"tool": "1/4 inch Allen wrench", "reset": "Red button on bottom of unit", "common_brands": ["InSinkErator", "Waste King", "Moen"], "hp_options": ["1/3 HP", "1/2 HP", "3/4 HP", "1 HP"]}', '["Jammed by bones, fruit pits, or utensils", "Overload tripped (red button popped out)", "Leaking from bottom (internal seal = replace unit)"]', '["1/4 inch Allen wrench (included with disposal)", "Flashlight", "Tongs or pliers (never hands!)"]', 'NEVER put hands inside disposal. Use Allen wrench from bottom to free jam, then tongs to remove debris from top. If leaking from bottom, the internal seal is gone — replace the whole unit. 3/4 HP is the sweet spot for most homes.', true),
('appliance', 'parts', 'Ice Maker Water Inlet Valve', 'The water inlet valve controls water flow to ice maker and dispenser. Solenoid-operated. Common failure = no ice production or small/hollow cubes.', '{"location": "Back of fridge, bottom panel", "voltage": "120V AC", "symptoms": {"no_ice": "Valve not opening", "small_cubes": "Low water pressure or failing valve"}, "common_part": {"whirlpool": "WPW10408179", "ge": "WR57X10032"}}', '["Frozen fill tube (not valve fault)", "Low home water pressure (<20 psi)", "Mineral buildup in valve screen"]', '["Multimeter", "1/4 inch nut driver", "Towels", "Bucket"]', 'Before replacing valve, check: 1) Water supply turned on? 2) Water pressure above 20 psi? 3) Fill tube frozen? (Use hair dryer to thaw). Check for 120V at valve when ice maker calls for water to confirm its the valve.', true),
('appliance', 'error_codes', 'Bosch Dishwasher E-Codes', 'Bosch dishwasher: E15=leak detected (base flood switch), E24/E25=drain issue, E09=heating element, E01=control board.', '{"E15": "Water in base pan (flood protection)", "E24": "Drain hose kinked or blocked", "E25": "Drain pump impeller blocked", "E09": "Heating element fault", "brand": "Bosch"}', '["E15 from tipping during install (water entered base)", "E24 from high drain loop missing", "E25 from glass or food debris in pump"]', '["Torx T20", "Towels", "Wet/dry vacuum", "Multimeter"]', 'For E15: tilt dishwasher back 45 degrees to drain water from base pan, then lay flat. Check for source of leak. Bosch requires high drain loop or air gap — no direct drain to disposal.', true),

('general', 'screws', 'Screw Types and Uses', 'Common screws: drywall (#6 coarse for wood studs, fine for metal studs), deck (exterior coated), cabinet (washer-head), concrete (Tapcon). Drive types: Phillips, square, Torx, hex.', '{"drywall": {"size": "#6", "lengths": ["1-1/4 inch", "1-5/8 inch", "2 inch"]}, "deck": {"size": "#8-#10", "coating": "ACQ-rated"}, "tapcon": {"sizes": ["3/16 inch", "1/4 inch"], "drill_bit": "included"}}', '["Wrong screw length for material thickness", "Using interior screws outdoors (rust)", "Stripping Phillips heads"]', '["Drill/driver", "Impact driver", "Bit set", "Countersink bit"]', 'Use square or Torx drive screws when possible — they strip far less than Phillips. For pressure-treated lumber, use ACQ-rated screws. Tapcons need the exact masonry bit size included in the box.', true),
('general', 'anchors', 'Drywall Anchor Guide', 'Anchor types by weight: plastic expansion (10 lb), self-drilling (25 lb), toggle bolt (50+ lb), snap toggle (50+ lb). Always prefer hitting a stud for heavy items.', '{"plastic_expansion": "10 lb max", "self_drilling": "25 lb max", "toggle_bolt": "50+ lb", "snap_toggle": "50+ lb", "stud_mount": "No limit for typical household items"}', '["Anchor spinning in oversized hole", "Using wrong anchor for weight", "Toggle bolt cant be removed and reinstalled"]', '["Drill", "Stud finder", "Level", "Appropriate drill bits"]', 'For TVs and heavy shelves, ALWAYS mount to studs. If must use drywall, snap toggles (SnapToggle brand) are the strongest and can be removed/reinstalled unlike regular toggle bolts. Use a stud finder first.', true),
('general', 'anchors', 'Concrete and Masonry Anchors', 'Concrete anchors: Tapcon screws (light-medium), wedge anchors (heavy permanent), sleeve anchors (medium, block walls), drop-in anchors (threaded insert).', '{"tapcon": {"use": "Light to medium", "removable": true}, "wedge": {"use": "Heavy loads in solid concrete", "removable": false}, "sleeve": {"use": "Block walls or solid concrete", "removable": true}}', '["Hole too deep or shallow for anchor", "Hitting rebar", "Block wall cracking from wedge anchor (use sleeve instead)"]', '["Hammer drill", "Correct masonry bit", "Compressed air (clean holes)", "Safety glasses", "Dust mask"]', 'Always drill 1/2 inch deeper than anchor length. Use hammer drill mode for concrete, rotation only for block. Clean dust from holes with compressed air before inserting anchors. Never use wedge anchors in hollow block.', true),
('general', 'caulk', 'Caulk and Sealant Selection', 'Silicone: bathrooms and exterior (waterproof, flexible, not paintable). Latex/acrylic: interior trim and gaps (paintable). Polyurethane: exterior, high movement joints. Butyl: gutters and metal.', '{"silicone": {"paintable": false, "wet_areas": true, "life": "20+ years"}, "latex": {"paintable": true, "wet_areas": false, "life": "5-10 years"}, "polyurethane": {"paintable": true, "wet_areas": true, "life": "15+ years"}}', '["Using latex caulk in shower (mold and failure)", "Caulking over old caulk", "Silicone not adhering to wet surface"]', '["Caulk gun", "Utility knife", "Painters tape", "Caulk removal tool", "Damp rag"]', 'Always remove old caulk completely before reapplying. Cut caulk tube at 45 degrees. Use painters tape for clean lines. GE Silicone II is the gold standard for bathrooms. Surface must be completely dry for silicone.', true),
('general', 'caulk', 'Spray Foam Insulation Types', 'Expanding foam: minimal (window/door — use only this around frames), standard (gaps/cracks), max fill (large voids). Never use standard around windows — it warps frames.', '{"minimal_expansion": {"use": "Windows and doors", "brand": "Great Stuff Window & Door"}, "standard": {"use": "Gaps and cracks", "expansion": "Up to 1 inch"}, "max_fill": {"use": "Large voids", "expansion": "Up to 3 inches"}}', '["Using regular foam around windows (warps frame)", "Over-applying (difficult to remove)", "Foam on skin or clothes (acetone to remove uncured)"]', '["Foam gun (for pro cans)", "Utility knife", "Acetone (cleanup)", "Gloves"]', 'ONLY use Window & Door (minimal expansion) foam around window and door frames. Regular expanding foam WILL warp frames and void window warranties. Wear gloves — cured foam does not come off skin easily.', true),
('general', 'paint', 'Interior Paint Sheen Guide', 'Paint sheens from least to most shiny: flat/matte, eggshell, satin, semi-gloss, high-gloss. Higher sheen = more washable but shows more imperfections.', '{"flat": "Ceilings, low-traffic walls", "eggshell": "Living rooms, bedrooms", "satin": "Kitchens, bathrooms, kids rooms", "semi_gloss": "Trim, doors, cabinets", "high_gloss": "Accent, furniture"}', '["Wrong sheen for application", "Flashing from uneven application", "Sheen looks different from sample (lighting)"]', '["Roller (3/8 nap for smooth, 1/2 for texture)", "2.5 inch angled brush", "Painters tape", "Drop cloths"]', 'Satin is the most versatile — works almost everywhere. Use flat only on ceilings. Two coats minimum over primer. Benjamin Moore Advance or Sherwin-Williams Emerald Urethane for trim and cabinets.', true),
('general', 'paint', 'Primer Selection Guide', 'Primers: latex (general purpose), shellac (stain/odor blocking — Zinsser BIN), oil-based (exterior wood — Zinsser Cover Stain), bonding (slick surfaces — Zinsser 123 Plus).', '{"latex": {"use": "New drywall, general purpose", "brand": "Zinsser 123"}, "shellac": {"use": "Stain and odor blocking", "brand": "Zinsser BIN"}, "oil": {"use": "Exterior wood, tannin blocking", "brand": "Zinsser Cover Stain"}}', '["Tannin bleed-through on cedar/redwood (need oil primer)", "Water stains showing through latex primer", "Skipping primer over patches"]', '["Roller", "Brush", "Respirator for shellac/oil primers"]', 'Zinsser BIN shellac primer blocks anything — water stains, smoke, pet odors, marker. Its the nuclear option for stain blocking. Use a respirator — strong fumes. One coat is usually sufficient.', true),
('general', 'screws', 'Nail and Brad Types', 'Framing: 16d common or 3.5" framing nails. Finish: 15 or 16 gauge brad nails (trim). Pin nails: 23 gauge (delicate trim). Roofing: 1.25" coil nails.', '{"framing": {"size": "16d (3.5 inch)", "gauge": "0.131 inch"}, "finish_15ga": {"use": "Heavy trim, baseboards", "holding": "High"}, "finish_16ga": {"use": "General trim", "holding": "Medium"}, "pin_23ga": {"use": "Delicate trim, no fill needed", "holding": "Low"}}', '["Wrong nail length (too short = weak, too long = blow-through)", "Using brads where framing nails needed", "Nail gun depth set wrong"]', '["Framing nailer", "Finish nailer (15 or 16 ga)", "Pin nailer (23 ga)", "Compressor or battery pack"]', '15 gauge angled finish nailer is the most versatile trim tool. 23 gauge pin nails leave virtually no hole — great for small moldings. For baseboards, use 15 gauge 2-inch into studs.', true),
('general', 'screws', 'Lag Bolts and Structural Fasteners', 'Lag bolts (lag screws): heavy structural connections. Common sizes: 1/4" x 3", 5/16" x 3.5", 3/8" x 4". Always pre-drill pilot and clearance holes.', '{"1_4_inch": {"pilot_drill": "3/16 inch", "use": "Ledger boards, heavy shelving"}, "5_16_inch": {"pilot_drill": "7/32 inch", "use": "Deck ledger, structural"}, "3_8_inch": {"pilot_drill": "1/4 inch", "use": "Heavy structural"}}', '["No pilot hole causing wood split", "Wrong size washer (must use flat washer)", "Over-torquing and stripping wood"]', '["Socket wrench or impact driver with socket adapter", "Drill with correct pilot bit", "Flat washers"]', 'Always use a flat washer under lag bolt head. Pre-drill pilot hole slightly smaller than lag bolt shaft. Do NOT use an impact driver at full speed — snugs plus 1/4 turn. For deck ledgers, use structural screws (GRK RSS) instead.', true),
('general', 'anchors', 'Stud Finding and Wall Composition', 'Wood studs: 16" on center (some 24"). Metal studs: commercial/newer apartments. Plaster/lath: older homes, harder to anchor. Use magnet stud finder for all types.', '{"wood_stud_spacing": "16 inch OC (standard)", "metal_stud": "Commercial and multi-family", "plaster_thickness": "3/4 to 1 inch over lath", "drywall_thickness": "1/2 or 5/8 inch"}', '["Stud finder giving false readings on plaster walls", "Metal stud not supporting screw-in fasteners", "Pipes and wires behind walls"]', '["Stud finder (magnetic or electronic)", "Strong magnet", "Small drill bit (probe)", "Flashlight"]', 'A strong rare-earth magnet is the most reliable stud finder — it finds drywall screws in studs. On plaster walls, use a magnet to find lath nails. Studs are reliably 16" from corners, windows, and doors.', true);
